export const getDataLocalstorage = (name) => {
    const saved = localStorage.getItem(name);
  const initialValue = JSON.parse(saved);
  return initialValue || [];
};


export const setDataLocalstorage = (name, data) => {
    localStorage.setItem(name, JSON.stringify(data));
};
