exports.upperCase = (yourString, letterIndex) =>
  [...yourString].reduce(
    (acc, letter, index) =>
      index === letterIndex ? acc + letter.toUpperCase() : acc + letter,
    "",
  );

exports.lowerCase = (yourString, letterIndex) =>
  [...yourString].reduce(
    (acc, letter, index) =>
      index === letterIndex ? acc + letter.toLowerCase() : acc + letter,
    "",
  );

exports.changeCapitalization = (chosenFunction, yourString, letterIndex) =>
  chosenFunction(yourString, letterIndex);
